var path = require("path");module.exports = function(bs){var options = bs.options;var server = options.server;var cwd = bs.cwd;if(server){if(Array.isArray(server.baseDir)){server.baseDirs = options.server.baseDir.map(function(item){return path.join(cwd,item)})}else{server.baseDirs = [path.join(cwd,server.baseDir)]}}};