"use strict";var gray=require("ansi-gray"),timestamp=require("time-stamp"),supportsColor=require("color-support");function hasFlag(r){return-1!==process.argv.indexOf("--"+r)}function addColor(r){return hasFlag("no-color")?r:hasFlag("color")?gray(r):supportsColor()?gray(r):r}function getTimestamp(){return"["+addColor(timestamp("HH:mm:ss"))+"]"}function log(){var r=getTimestamp();return process.stdout.write(r+" "),console.log.apply(console,arguments),this}function info(){var r=getTimestamp();return process.stdout.write(r+" "),console.info.apply(console,arguments),this}function dir(){var r=getTimestamp();return process.stdout.write(r+" "),console.dir.apply(console,arguments),this}function warn(){var r=getTimestamp();return process.stderr.write(r+" "),console.warn.apply(console,arguments),this}function error(){var r=getTimestamp();return process.stderr.write(r+" "),console.error.apply(console,arguments),this}module.exports=log,module.exports.info=info,module.exports.dir=dir,module.exports.warn=warn,module.exports.error=error;