"use strict";var format=require("util").format,sparkles=require("sparkles"),levels=["debug","info","warn","error"];function getLogger(e){var r=sparkles(e);return levels.forEach(function(e){r[e]=makeLogLevel(r,e)}),r}function makeLogLevel(e,r){return function(t){"string"==typeof t&&(t=format.apply(null,arguments)),e.emit(r,t)}}module.exports=getLogger;